import 'package:flutter/cupertino.dart';
import 'package:flutter/material.dart';

class page3 extends StatelessWidget {
  const page3({Key? key}) : super(key: key);

  @override
  Widget build(BuildContext context) {
    return SafeArea(
        child: Scaffold(
      appBar: AppBar(),
      body: SingleChildScrollView(
          child: Text(
              "প্রাকইতিহাস  বাংলা ‘বেসিন’-এর অনেক অংশে প্রাগৈতিহাসিক ও প্রায়-ঐতিহাসিক মানব বসতি ও সংস্কৃতির নিদর্শন আবিষ্কৃত হয়েছে। তৎকালীন সংস্কৃতি সম্পর্কিত নির্ভরযোগ্য ও অর্থপূর্ণ অনুমান  এবং এর কাল নিরূপণের জন্য এ সমস্ত সাক্ষ্যগুলিকে- বিশেষ করে হাতিয়ার, ব্যবহূত বস্ত্ত, অন্যান্য প্রত্ননিদর্শন ইত্যাদিকে ভূমিরূপ, মৃত্তিকা ও পললের সাথে ভূতাত্ত্বিক ও ভূ-গঠন প্রক্রিয়ার সাথে সমন্বয় সাধনের চেষ্টা করা হয়েছে। কিন্তু প্রাগৈতিহাসিক ও প্রায়-ঐতিহাসিক মানব বসতি ও সংস্কৃতির গতি-প্রকৃতি পুনর্গঠনের ক্ষেত্রে পুরোপুরি সাফল্য লাভ করা যায় নি। তবে এ ব্যাপারে প্রত্নতাত্ত্বিকগণ গবেষণা চালিয়ে যাচ্ছেন।বাংলাদেশের প্রাকইতিহাস এদেশের চারিদিকে অবস্থিত বিভিন্ন দেশের, বিশেষ করে আসাম, অরুণাচল প্রদেশ, মনিপুর, নাগাল্যান্ড, মেঘালয়, মিজোরাম, ত্রিপুরা, বিহার, উড়িষ্যা, পশ্চিম বাংলা এবং মায়ানমারের প্রাকইতিহাসের সাথে সম্পর্কযুক্ত। বাংলাদেশের বর্তমান সীমারেখার মধ্যে প্রাগৈতিহাসিক অনুসন্ধান চারিদিকে অবস্থিত পার্শ্ববর্তী দেশগুলির তুলনায় অপেক্ষাকৃত নবীন ও সীমিত। তাছাড়া আসাম, বিহার এবং বঙ্গে প্রাপ্ত প্রাগৈতিহাসিক রেকর্ডসমূহ বেশির ভাগ ক্ষেত্রে অবিন্যস্ত। তাই এখন পর্যন্ত এ অঞ্চলের প্রাগৈতিহাসিক সংস্কৃতি সম্বন্ধে খুব বেশি জানা যায় নি।বিশ শতকের ষাট দশকে উত্তর-পূর্ব ভারতে (আসাম, অরুণাচল, মনিপুর, মেঘালয়, মিজোরাম, নাগাল্যান্ড, ত্রিপুরা) বিজ্ঞানভিত্তিক প্রাগৈতিহাসিক ও প্রায়-ঐতিহাসিক নিদর্শনের জন্য অনুসন্ধান শুরু হয়। এ সময়ে আসামের উত্তর কাছার পাহাড়ে লাংথিং এবং মহুর নদী উপত্যকায় এইচ.ডি সাংকালিয়ার নেতৃত্বে এক দল প্রত্নতত্ত্ববিদ অনুসন্ধান পরিচালনা করেন এবং দাউঝালি হাডিং নামে স্তরায়িত নবোপলীয় প্রত্নস্থল চিহ্নিত করেন। এখানে পরিচালিত উৎখননে একটি মসৃণ প্রস্তর হাতিয়ার শিল্প, একটি মৃৎপাত্র শিল্প ও বেশ কিছু গৃহসামগ্রী যেমন কর্ন গ্রাইন্ডার, স্টোন রাবার, ম্যুলার উম্মোচিত হয়। গৌহাটি বিশ্ববিদ্যালয়ের নৃবিজ্ঞান বিভাগ গারো পাহাড়ের গানোল, রংগ্রাম এবং সিমসাং নদী উপতক্যায় অনুসন্ধান পরিচালনা করে এবং প্রস্তর যুগের সকল পর্বের সাংস্কৃতিক বস্ত্ত সম্বনিত অনেক প্রত্নস্থল সনাক্ত করে।প্রত্নস্থলগুলির মধ্যে সেলবাগিরি, থেব্রোংগিরি, মিসিমাগিরি, মোকবল আব্রি ও রংগ্রাম আলাগিরিতে পরীক্ষামূলক উৎখনন করা হয়। থেব্রোংগিরি, মোকবল আব্রি ও মিসিমাগিরি-২ এ ডলেরাইটের তৈরি কিছু হাতকুঠার, ক্লিভার ও বড় ডিসকয়েডসহ প্রচুর কোর, ফ্লেক ও অসম্পূর্ণ হাতিয়ার উন্মোচন করা হয়। মিসিমাগিরি-৩ এবং ডিডামি থেকে বিভিন্ন রকম ব্লেড ও প্রচুর পরিমাণ ব্লেডফ্লেকসহ বেশ কিছু ফ্লুটেড কোর আবিষ্কৃত হয়। গারো পাহাড়ে আর এক ধরনের ভিন্ন সাংস্কৃতিক ঐতিহ্যের প্রস্তর বস্ত্ত পাওয়া যায়, যা পূর্বাঞ্চলীয় পেব্ল ঐতিহ্য নামে পরিচিত। সিমসাং-নাঙ্গাল উপত্যকার নাঙ্গালবিব্রায়, ডলেরাইট ও চার্টের নুড়ির তৈরি এ সংস্কৃতির পেব্ল ও ফ্লেক হাতিয়ার চিহ্নিত করা হয়েছে।সেলবাগিরি ও থেব্রোংগিরিতে অবস্থিত প্রত্নস্থলগুলোর ভূপৃষ্ঠে মাইক্রোলিথ পাওয়া যায়। পরে রংগ্রাম উপত্যকার সেলবাগিরি গ্রামের নিকট পুরাতন নদী সোপানে উৎখননে দেখা যায় যে, মাইক্রোলিথিক স্তরের অবস্থান নবোপলীয় স্তরের নিচে। এখানকার উল্লেখযোগ্য নবোপলীয় হাতিয়ার হচ্ছে গ্রাউন্ড এবং চিপ্ড এক্স (হাতকুঠার) ও হাতের তৈরি ধূসর সাধারণ মৃৎপাত্র। রংগ্রাম আলাগিরি উৎখননে দুটি সাংস্কৃতিক স্তর অবমুক্ত করা হয়: (১) গোলাকৃতির বাটযুক্ত হাতকুঠারের উপস্থিতি ও সোল্ডার হাতকুঠারের অনুপস্থিতিতে বৈশিষ্টায়িত উপরের স্তরের নবোপলীয় সংস্কৃতি এবং (২) মৃৎপাত্রবিহীন, ভারী প্রস্তর পাউল্ডারসহ পেব্ল হাতকুঠার ও চপার সম্বলিত এবং নবোপলীয় স্তরের নিচে অবস্থিত হোবিনহিয়ান সংস্কৃতি। রংগ্রাম নদীর বাম তীরে ও রেনচাঙ্গগিরি গ্রামের ২ কিমি দক্ষিণ-পশ্চিমে চিত্রা আব্রিতে উৎখননে দুটি বড় প্রস্তর যুগীয় শিল্প উন্মোচিত হয়। এদের একটি হচ্ছে ফ্লেক-ব্লেড শিল্প এবং অপরটি হচ্ছে হাতে তৈরি মৃৎপাত্রসহ নবোপলীয় গ্রাউন্ড-প্রস্তর কুঠার শিল্প।চিত্রা আব্রি প্রত্নবস্ত্তসমূহের বৈশিষ্ট্য হলো এর মধ্যে উল্লেখযোগ্য পরিমাণে সোল্ডার্ড সেল্ট রয়েছে এবং এর সবগুলি হাতিয়ারই ডলেরাইট দ্বারা তৈরি। আসামের কামরূপ জেলার সারুতরু নামক স্থানে ডিব্রুগড় বিশ্ববিদ্যালয় উৎখনন কার্য পরিচালনা করে এবং নবোপলীয় সংস্কৃতির হাতিয়ার উন্মোচন করে।  আর্কিওলজিক্যাল সার্ভে অব ইন্ডিয়া এর প্রাগৈতিহাসিক শাখার বি.পি বোপারডিকার অরুণাচল প্রদেশের লুহিত জেলার ডেফাবুম অঞ্চলে অনুসন্ধানকার্য পরিচালনা করেন এবং তিনি এ অঞ্চলে পুরোপলীয় ও নবোপলীয় ঐতিহ্যের হাতিয়ার আবিষ্কার করেন। আর্কিওলজিক্যাল সার্ভে অব মনিপুর স্টেট এর ও.কে সিং এ প্রদেশের বিভিন্ন এলাকায় জরিপ চালান এবং উখরুলের নিকট বেশ কিছু চুনাপাথরের গুহা আবিষ্কার করেন। তিনি ৩নং গুহায় একটি পরিখা খনন করে হাড় ও পাথরের তৈরি হাতিয়ার পান। মিজোরাম থেকে একটি মাত্র পাথরের কুঠারের কথা জানা যায়। নাগাল্যান্ডের কেন্দ্রীয় অংশে সেমা ও লহোটা অঞ্চলে ভূ-পৃষ্ঠীয় ডাইওরাইট/সারপেন্টাইন পাথরের হাতিয়ারের সংগ্রহ (অবশ্য কিছু হাতিয়ার শেল, সিস্ট, বেলেপাথর দ্বারা তৈরি) এ প্রদেশের প্রাগৈতিহাসিক যুগের নির্দেশ করে। এই সংগৃহীত হাতিয়ারগুলিকে দুটো গ্রুপে ভাগ করা হয়েছে: একটি হচ্ছে সোল্ডারড্ / ট্যাঙ্গড সেল্ট সমৃদ্ধ এবং অপরটি ত্রিকোণাকার/ পয়েন্টেড বাটযুক্ত কুঠার সমৃদ্ধ।বদ্বীপীয় সমতল দেশ বাংলাদেশকে ছাড়িয়ে উত্তর-পূর্ব ভারতের সবচেয়ে দক্ষিণ-পূর্বাঞ্চলীয় সীমানা প্রদেশ ত্রিপুরার খোয়াই ও হাওড়া নদী উপত্যকায় জিওলজিক্যাল সার্ভে অব ইন্ডিয়ার এন.আর রমেশ অর্ধ ডজনের উপরে হাতিয়ার সমৃদ্ধ প্রত্নস্থল শনাক্ত করেন। তিনি এ অঞ্চলের বিভিন্ন অবস্থান যেমন তেলিয়ামুড়া, জিরানিয়া, মোহনপুর, বিশালগড় ও আগরতলা থেকে ৭০০ এরও বেশি প্রস্তর যুগীয় হাতিয়ার সংগ্রহ করেন। ত্রিপুরার প্রাকইতিহাসের সবচেয়ে গৌরবোজ্জ্বল বৈশিষ্ট্য হচ্ছে, এখানকার সবগুলো হাতিয়ারই স্থানীয়ভাবে সহজলভ্য প্রস্তরীভূত জীবাশ্ম কাঠের তৈরি। এ হাতিয়ারগুলিকে দুটি ভাগে ভাগ করা হয়েছে: (ক) নবোপলীয় হাতিয়ারের সাথে সাদৃশ্যপূর্ণ পোলিশ্ড এক্স বিহীন প্রাক-নবোপলীয় হাতিয়ার ও (খ) পোলিশ্ড এক্স সমৃদ্ধ নবোপলীয় হাতিয়ারসমূহ। প্রাক-নবোপলীয় হাতিয়ারসমূহ শেষ প্লাইস্টোসিন-হলোসিন যুগের (পুরাতন ও নতুন টেরেসের কালনিরূপিত স্তরিভূত অবক্ষেপসমূহের রেকর্ড থেকে বুঝা যায় যে এদের বয়স ৩৫০০০ বছরের চেয়ে অনেক কম এবং ৩৫০০ বছরের চেয়ে পুরাতন)। এই প্রত্নবস্ত্তসমূহ বার্মার ইরাবতী নদী উপত্যকায় প্রাপ্ত অ্যানাথিয়ান ও নবোপলীয় প্রত্নবস্ত্তসমূহের সাথে সাদৃশ্যপূর্ণ।ছোট নাগপুর মালভূমি পালামৌ, রাঁচি, হাজারীবাগ, মুঙ্গের, গয়া এবং সিংভূম জেলাগুলির বিভিন্ন অবস্থান থেকে নিম্ন পুরোপলীয় যুগের হাতিয়ার আবিষ্কৃত হয়েছে। পালামৌ জেলায় ঠিক স্তরায়িত প্রেক্ষাপটে কোন নিম্ন বা মধ্য পুরোপলীয় হাতিয়ার পাওয়া যায় নি। দিলীপ কুমার চক্রবর্তী পরিবর্তিত ধরণের কিছু প্রত্নবস্ত্তর কথা উল্লেখ করেছেন। তিনি এবং এ.কে ঘোষ ৭টি উচ্চ পুরোপলীয় প্রত্নস্থল (এ প্রত্নস্থলসমূহে প্রাপ্ত হাতিয়ারসমূহ হচ্ছে কোয়ার্টজ এবং চার্টের তৈরি পয়েন্ট, ব্লেড, বিউরিন, ছুরি ও সুঁচ) আবিস্কার করেছেন। হাজারীবাগ-গিরিড অঞ্চলের প্রাগৈতিহাসিক গুরুত্ব স্পষ্ট নয়। এখানে চারটি নিম্ন ও চারটি মধ্য এবং বেশ কিছু উচ্চ পুরোপলীয় সংস্কৃতির অবস্থানের কথা জানা গেছে। এ প্রত্নস্থলসমূহে কোয়ার্টজের তৈরি ফ্লেক, হাতকুঠার, স্ক্রেপার, বোরার, পয়েন্ট ইত্যাদি পাওয়া যায়। বিদুলা জয়াসাল রাঁচি জেলার নয়টি প্রত্নস্থল থেকে প্রাপ্ত ত্রিশটি নতুন হাতিয়ারের কথা উল্লেখ করেছেন। এ হাতিয়ারসমূহ হলো চপিং হাতিয়ার, হাতকুঠার, স্ক্রেপার, ফ্লেক, কোর, ক্লিভার ইত্যাদি। এ.কে ঘোষ উচ্চ সুবর্ণরেখা নদী উপত্যকায় অবস্থিত এই জেলার কিছু নির্দিষ্ট অঞ্চলে অনুসন্ধান পরিচালনা করেন এবং প্রস্তর হাতিয়ার সমেত বেশ কিছু প্রত্নস্থল চিহ্নিত করেন। দিলীপ কুমার চক্রবর্তীও এই জেলায় কিছু প্রস্তর হাতিয়ারের অবস্থান আবিষ্কার করেন।রাঁচি অঞ্চলের প্রস্ত্তর যুগীয় শিল্পে মূলত উচ্চপুরোপলীয় হাতিয়ারের সমাবেশ ও মাইক্রোলিথ এবং কিছু নিম্নপুরোপলীয় উপাদান বিদ্যমান। এ হাতিয়ারগুলি সাধারণত কোয়ার্টজ দ্বারা তৈরি এবং কিছু কিছু হাতিয়ার ধূসর-কালো চার্ট দ্বারা নির্মিত। প্রত্নতত্ত্ববিদগণ সিংভূম জেলায় ৪০টি প্রাগৈতিহাসিক প্রত্নস্থল চিহ্নিত করেছেন। এ অঞ্চলের হাতিয়ারগুলির মধ্যে কোয়ার্টজাইটের তৈরি নিম্নপুরোপলীয় হাতিয়ার (হাতকুঠার, চপার, চপিং হাতিয়ার, ক্লিভার, ডিসকয়েড, কোর, ইত্যাদি) এবং মিহিকণা কোয়ার্টজাইটের তৈরি মধ্য পুরোপলীয় হাতিয়ার (বোরার, পয়েন্ট, বিভিন্ন ধরনের স্ক্রেপার, রিটাচ্ড ফ্লেক ইত্যাদি) ও উচ্চ পুরোপলীয় হাতিয়ার (ব্লেড, রিটাচ্ড ব্লেড, বিউরিন, পয়েন্ট ইত্যাদি) রয়েছে। দিলীপ কুমার চক্রবর্তী সাঁওতাল পরগনা জেলার পশ্চিমাংশের উঁচু-নিচু ভূভাগে বিচ্ছিন্ন কিছু নিম্নপুরোপলীয় হাতিয়ারের অবস্থান সনাক্ত করেছেন। তিনি রাজমহল ও দামিন অঞ্চলে উচ্চ পুরোপলীয় শিল্পের ব্যাপক অবস্থান সম্পর্কে নিশ্চিত হন। তিনি দামিন শিল্পকে একটি আঞ্চলিক উচ্চ পুরোপলীয় শিল্প বলে অভিহিত করেন। এ ছাড়াও বিহারের মুঙ্গেরের নিকট খড়গপুর রেঞ্জে অবস্থিত পায়েসরায় লোকেলিটি এফ এ ভূ-পৃষ্ঠ থেকে ৬৫-৯০ সেমি নিচে ১০৫ বর্গ মিটার আয়তনের একটি মেসোলিথিক বসতি ফ্লোর উৎখননের মাধ্যমে অবমুক্ত করা হয়েছে এবং এই উৎখননে ২৬টি ফিনিস্ড মাইক্রোলিথ হাতিয়ার (লুনেট, সাইড স্ক্রেপার, বেকড ব্লেড ইত্যাদি ও একটি মাইক্রো-গ্যাভিটি পয়েন্ট) পাওয়া গেছে। এ প্রত্নস্থলটির রেডিও কার্বন ডেটিং অনুসারে এ প্রত্নস্থলের সম্ভাব্য সময় নির্ধারন করা হয় ৬৩৭৭ খ্রিস্টপূর্ব থেকে ৬০৬৭ খ্রিস্টপূর্ব। ছোটনাগপুর অঞ্চল থেকে কোন নির্ভরযোগ্য নবোপলীয় প্রত্নস্থল আবিষ্কৃত হয় নি, যদিও উত্তর বিহারের অ্যালুভিয়াল প্লেনের বিভিন্ন প্রত্নস্থলে (চিরান্ড, সেনার, তারাদি ইত্যাদি) নবোপলীয় স্তরের প্রাচুর্য রয়েছে।পশ্চিমবঙ্গে প্রাগৈতিহাসিক ও প্রায়-ঐতিহাসিক প্রেক্ষাপট ভূ-তাত্ত্বিকভাবে, পশ্চিমবঙ্গ (২১°৩৪´ থেকে ২৭°১০´ উত্তর, ৮৫°৫০´ থেকে ৮৯°০৫´ পূর্ব) এক বিচিত্র ভূমিরূপের সমন্বয়: উত্তরে হিমালয়, দক্ষিণে বঙ্গোপসাগর, পশ্চিমে ক্ষয়জাত পাহাড়শ্রেণী, পূর্বে নদীবিধৌত সমতল। এই চারটি অঞ্চল আবার চারটি ভিন্ন প্রতিবেশমন্ডলের আওতাভুক্ত। ভূমির মোট পরিমাণ ৮৭.৬১৬ বর্গ কিমি। পশ্চিমবঙ্গের দক্ষিণ-পশ্চিমাঞ্চলে পাওয়া গেছে পুরোপলীয় সংস্কৃতির নিদর্শন। এই অঞ্চলে পড়েছে বাঁকুড়া, পুরুলিয়া, বীরভূম ও বর্ধমান জেলা এবং মেদিনীপুরের দক্ষিণ-পশ্চিমাংশ। বিভিন্ন নদী, যেমন কসাই বা কংসবতী, সুবর্ণরেখা, ময়ুরাক্ষী, অজয়, দামোদর, গন্ধেশ্বরী প্রভৃতি এবং এদের শাখা নদীসমূহ জালের মত জড়িয়ে আছে এই বিশাল এবড়ো-থেবড়ো ভূমি জুড়ে। পুরোপলীয় প্রত্নস্থলগুলির অবস্থান মূলত ৫০ সমোন্নতি রেখার উপরে, বিভিন্ন পর্বত ঢালে, ছোট পাহাড়ে কিংবা উঁচু স্থানে এবং নদী তীরে।")),
    ));
  }
}
